import React from 'react';
import { FilterState } from '../types';

interface FilterPanelProps {
  filters: FilterState;
  setFilters: React.Dispatch<React.SetStateAction<FilterState>>;
}

const specialities = [
  'General Physician',
  'Dentist',
  'Dermatologist',
  'Paediatrician',
  'Gynaecologist',
  'ENT',
  'Diabetologist',
  'Cardiologist',
  'Physiotherapist',
  'Endocrinologist',
  'Orthopaedic',
  'Ophthalmologist',
  'Gastroenterologist',
  'Pulmonologist',
  'Psychiatrist',
  'Urologist',
  'Dietitian/Nutritionist',
  'Psychologist',
  'Sexologist',
  'Nephrologist',
  'Neurologist',
  'Oncologist',
  'Ayurveda',
  'Homeopath'
];

const FilterPanel: React.FC<FilterPanelProps> = ({ filters, setFilters }) => {
  const handleConsultationTypeChange = (type: 'Video Consult' | 'In Clinic') => {
    setFilters(prev => ({
      ...prev,
      consultationType: prev.consultationType === type ? '' : type
    }));
  };

  const handleSpecialityChange = (speciality: string) => {
    setFilters(prev => ({
      ...prev,
      specialities: prev.specialities.includes(speciality)
        ? prev.specialities.filter(s => s !== speciality)
        : [...prev.specialities, speciality]
    }));
  };

  const handleSortChange = (sortBy: 'fees' | 'experience') => {
    setFilters(prev => ({
      ...prev,
      sortBy: prev.sortBy === sortBy ? '' : sortBy
    }));
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow">
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-3" data-testid="filter-header-moc">
          Consultation Mode
        </h3>
        <div className="space-y-2">
          <label className="flex items-center">
            <input
              type="radio"
              data-testid="filter-video-consult"
              checked={filters.consultationType === 'Video Consult'}
              onChange={() => handleConsultationTypeChange('Video Consult')}
              className="mr-2"
            />
            Video Consult
          </label>
          <label className="flex items-center">
            <input
              type="radio"
              data-testid="filter-in-clinic"
              checked={filters.consultationType === 'In Clinic'}
              onChange={() => handleConsultationTypeChange('In Clinic')}
              className="mr-2"
            />
            In Clinic
          </label>
        </div>
      </div>

      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-3" data-testid="filter-header-speciality">
          Speciality
        </h3>
        <div className="space-y-2 max-h-60 overflow-y-auto">
          {specialities.map(speciality => (
            <label key={speciality} className="flex items-center">
              <input
                type="checkbox"
                data-testid={`filter-specialty-${speciality.replace('/', '-')}`}
                checked={filters.specialities.includes(speciality)}
                onChange={() => handleSpecialityChange(speciality)}
                className="mr-2"
              />
              {speciality}
            </label>
          ))}
        </div>
      </div>

      <div>
        <h3 className="text-lg font-semibold mb-3" data-testid="filter-header-sort">
          Sort By
        </h3>
        <div className="space-y-2">
          <label className="flex items-center">
            <input
              type="radio"
              data-testid="sort-fees"
              checked={filters.sortBy === 'fees'}
              onChange={() => handleSortChange('fees')}
              className="mr-2"
            />
            Fees (Low to High)
          </label>
          <label className="flex items-center">
            <input
              type="radio"
              data-testid="sort-experience"
              checked={filters.sortBy === 'experience'}
              onChange={() => handleSortChange('experience')}
              className="mr-2"
            />
            Experience (High to Low)
          </label>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
